// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

contract Market {
    enum Outcome { Undecided, Yes, No }

    struct MarketState {
        bool exists;
        bool resolved;
        Outcome outcome;
        uint256 totalYes;
        uint256 totalNo;
    }

    mapping(uint256 => MarketState) public markets;
    mapping(uint256 => mapping(address => uint256)) public contributionYes;
    mapping(uint256 => mapping(address => uint256)) public contributionNo;

    address public owner;

    event MarketCreated(uint256 indexed marketId);
    event BetPlaced(uint256 indexed marketId, address indexed bettor, uint256 amount, bool onYes);
    event MarketResolved(uint256 indexed marketId, Outcome outcome);

    modifier onlyOwner() {
        require(msg.sender == owner, "only owner");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function createMarket(uint256 marketId) external onlyOwner {
        require(!markets[marketId].exists, "market exists");
        markets[marketId] = MarketState({ exists: true, resolved: false, outcome: Outcome.Undecided, totalYes: 0, totalNo: 0 });
        emit MarketCreated(marketId);
    }

    function placeBet(uint256 marketId, bool onYes) external payable {
        require(markets[marketId].exists, "no market");
        require(!markets[marketId].resolved, "market resolved");
        require(msg.value > 0, "no value");

        if (onYes) {
            contributionYes[marketId][msg.sender] += msg.value;
            markets[marketId].totalYes += msg.value;
        } else {
            contributionNo[marketId][msg.sender] += msg.value;
            markets[marketId].totalNo += msg.value;
        }

        emit BetPlaced(marketId, msg.sender, msg.value, onYes);
    }

    function resolve(uint256 marketId, Outcome outcome) external onlyOwner {
        require(markets[marketId].exists, "no market");
        require(!markets[marketId].resolved, "already resolved");
        require(outcome == Outcome.Yes || outcome == Outcome.No, "invalid");

        markets[marketId].resolved = true;
        markets[marketId].outcome = outcome;

        emit MarketResolved(marketId, outcome);
    }

    function claim(uint256 marketId) external {
        require(markets[marketId].exists && markets[marketId].resolved, "not settled");
        Outcome win = markets[marketId].outcome;
        uint256 winnerTotal = win == Outcome.Yes ? markets[marketId].totalYes : markets[marketId].totalNo;
        uint256 loserTotal = win == Outcome.Yes ? markets[marketId].totalNo : markets[marketId].totalYes;
        require(winnerTotal > 0, "no winners");

        uint256 userContribution = win == Outcome.Yes ? contributionYes[marketId][msg.sender] : contributionNo[marketId][msg.sender];
        require(userContribution > 0, "no contribution");

        // calculate payout: contribution + share of loser pool
        uint256 payout = userContribution + (userContribution * loserTotal) / winnerTotal;

        // zero out contribution to prevent re-claim
        if (win == Outcome.Yes) {
            contributionYes[marketId][msg.sender] = 0;
        } else {
            contributionNo[marketId][msg.sender] = 0;
        }

        payable(msg.sender).transfer(payout);
    }
}

contracts/PredictionMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./Reputation.sol";

contract PredictionMarket is Ownable {
    Reputation public reputationContract;
    uint256 public treasury;

    enum State { Open, Resolved }

    struct Market {
        string question;
        uint256 endsAt;
        State state;
        uint256 totalYesAmount;
        uint256 totalNoAmount;
        mapping(address => uint256) yesBets;
        mapping(address => uint256) noBets;
        address[] yesBetters;
        address[] noBetters;
        mapping(address => bool) isYesBetter;
        mapping(address => bool) isNoBetter;
    }

    Market[] public markets;

    event MarketCreated(uint256 indexed marketId, string question, uint256 endsAt);
    event BetPlaced(uint256 indexed marketId, address indexed better, bool outcome, uint256 amount);
    event MarketResolved(uint256 indexed marketId, bool winningOutcome);
    event PayoutTransferred(address indexed user, uint256 amount);

    constructor(address reputationAddress) Ownable() {
        reputationContract = Reputation(reputationAddress);
    }

    function createMarket(string memory _question, uint256 _duration) public onlyOwner {
        uint256 marketId = markets.length;
        Market storage newMarket = markets.push();
        newMarket.question = _question;
        newMarket.endsAt = block.timestamp + _duration;
        newMarket.state = State.Open;
        newMarket.totalYesAmount = 0;
        newMarket.totalNoAmount = 0;
        emit MarketCreated(marketId, _question, block.timestamp + _duration);
    }

    function placeBet(uint256 marketId, bool outcome) public payable {
        require(marketId < markets.length, "Market does not exist");
        Market storage market = markets[marketId];
        require(market.state == State.Open, "Market is not open");
        require(block.timestamp < market.endsAt, "Market has ended");
        require(msg.value > 0, "Bet amount must be greater than 0");

        if (outcome) { // Yes bet
            if (!market.isYesBetter[msg.sender]) {
                market.isYesBetter[msg.sender] = true;
                market.yesBetters.push(msg.sender);
            }
            market.yesBets[msg.sender] += msg.value;
            market.totalYesAmount += msg.value;
        } else { // No bet
            if (!market.isNoBetter[msg.sender]) {
                market.isNoBetter[msg.sender] = true;
                market.noBetters.push(msg.sender);
            }
            market.noBets[msg.sender] += msg.value;
            market.totalNoAmount += msg.value;
        }

        emit BetPlaced(marketId, msg.sender, outcome, msg.value);
    }

    function resolveMarket(uint256 marketId, bool winningOutcome) public onlyOwner {
        require(marketId < markets.length, "Market does not exist");
        Market storage market = markets[marketId];
        require(market.state == State.Open, "Market is not open");

        market.state = State.Resolved;
        emit MarketResolved(marketId, winningOutcome);

        uint256 totalPot = market.totalYesAmount + market.totalNoAmount;
        if (totalPot == 0) {
            return; // No bets placed, nothing to do.
        }

        // A 1% fee is sent to the treasury
        uint256 fee = totalPot / 100;
        treasury += fee;
        uint256 distributablePot = totalPot - fee;

        if (winningOutcome) { // Yes wins
            distributeWinnings(distributablePot, market.totalYesAmount, market.yesBetters, market.yesBets);
        } else { // No wins
            distributeWinnings(distributablePot, market.totalNoAmount, market.noBetters, market.noBets);
        }
    }

    function distributeWinnings(uint256 distributablePot, uint256 totalWinningAmount, address[] memory winners, mapping(address => uint256) storage bets) private {
        if (totalWinningAmount == 0) {
            // No winners, pot goes to treasury
            treasury += distributablePot;
            return;
        }

        for (uint i = 0; i < winners.length; i++) {
            address winner = winners[i];
            uint256 betAmount = bets[winner];
            uint256 payout = (betAmount * distributablePot) / totalWinningAmount;
            if (payout > 0) {
                payable(winner).transfer(payout);
                emit PayoutTransferred(winner, payout);
            }
        }
    }

    function withdrawFees() public onlyOwner {
        uint256 amount = treasury;
        require(amount > 0, "No fees to withdraw");
        treasury = 0;
        payable(owner()).transfer(amount);
    }
}

contracts/Reputation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

contract Reputation is Ownable {
    mapping(address => uint256) public reputations;

    event ReputationChanged(address user, uint256 newReputation);

    constructor() Ownable() {}

    function getReputation(address user) public view returns (uint256) {
        return reputations[user];
    }

    function setReputation(address user, uint256 reputation) public onlyOwner {
        reputations[user] = reputation;
        emit ReputationChanged(user, reputation);
    }

    function incrementReputation(address user, uint256 amount) public onlyOwner {
        reputations[user] += amount;
        emit ReputationChanged(user, reputations[user]);
    }

    function decrementReputation(address user, uint256 amount) public onlyOwner {
        if (reputations[user] >= amount) {
            reputations[user] -= amount;
        } else {
            reputations[user] = 0;
        }
        emit ReputationChanged(user, reputations[user]);
    }
}

